	public int find(int[] A,int[] B,int common) {
		if(common==-1)
			return -1;
		int[] labelA=new int[A.length];
		int[] labelB=new int[B.length];
		int sumA=0;
		int sumB=0;
		for(int i=0;i<A.length;i++)
			if(A[i]==common)
				labelA[i]=1;
			else {
				sumA++;
				labelA[i]=0;
			}
		for(int i=0;i<B.length;i++)
			if(B[i]==common)
				labelB[i]=1;
			else {
				sumB++;
				labelB[i]=0;
			}
		for(int i=0;i<A.length;i++) {
			if(labelA[i]==0&&labelB[i]==0) {
				return -1;
			}
		}
		return Math.min(sumA, sumB);
	}
	public int minDominoRotations(int[] A, int[] B) {
		if(A.length==1)
			return 1;
		//首先要找出公共的子元素，这时只需要考虑A和B数组中的前两个元素
		int common=-1;
		int AA=-1;
		int BB=-1;
		if(A[0]==A[1]||A[0]==B[1]) {
			common=A[0];
			AA=find(A, B, common);
		}
		if(B[0]==A[1]||B[0]==B[1]) {
			common=B[0];
			BB=find(A, B, common);
		}
		return Math.max(AA, BB);

    }
